contracts/CaseSale.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";
import { ReentrancyGuard } from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { VRFConsumerBaseV2 } from "@chainlink/contracts/src/v0.8/vrf/VRFConsumerBaseV2.sol";
import { VRFCoordinatorV2Interface } from "@chainlink/contracts/src/v0.8/vrf/interfaces/VRFCoordinatorV2Interface.sol";

contract CaseSale is VRFConsumerBaseV2, Ownable, ReentrancyGuard {
  using SafeERC20 for IERC20;

  struct CaseType {
    uint256 priceUSDC; // 6 decimals
    uint256 minReward; // cbBTC smallest unit (8 decimals)
    uint256 maxReward; // cbBTC smallest unit (8 decimals)
    bool enabled;
  }

  struct Opening {
    address buyer;
    uint256 caseTypeId;
    uint256 rewardAmount;
    bool rewarded;
    bool claimed;
    uint256 requestId;
  }

  IERC20 public immutable usdc;
  IERC20 public immutable cbBtc;
  address public treasury;

  VRFCoordinatorV2Interface public immutable vrfCoordinator;
  bytes32 public keyHash;
  uint64 public subscriptionId;
  uint16 public requestConfirmations;
  uint32 public callbackGasLimit;

  uint256 public nextOpeningId;

  mapping(uint256 => CaseType) public caseTypes;
  mapping(uint256 => Opening) public openings;
  mapping(uint256 => uint256) public requestToOpeningId;

  event CaseTypeUpdated(uint256 indexed caseTypeId, uint256 priceUSDC, uint256 minReward, uint256 maxReward, bool enabled);
  event CasePurchased(address indexed buyer, uint256 indexed caseTypeId, uint256 indexed openingId, uint256 priceUSDC);
  event CaseRandomnessRequested(uint256 indexed openingId, uint256 indexed requestId);
  event CaseRewarded(uint256 indexed openingId, uint256 rewardAmount);
  event CaseClaimed(uint256 indexed openingId, address indexed buyer, uint256 rewardAmount);
  event TreasuryUpdated(address indexed treasury);
  event VrfConfigUpdated(bytes32 keyHash, uint64 subscriptionId, uint16 requestConfirmations, uint32 callbackGasLimit);

  constructor(
    address usdcAddress,
    address cbBtcAddress,
    address treasuryAddress,
    address vrfCoordinatorAddress,
    bytes32 vrfKeyHash,
    uint64 vrfSubscriptionId,
    uint16 vrfRequestConfirmations,
    uint32 vrfCallbackGasLimit
  ) VRFConsumerBaseV2(vrfCoordinatorAddress) Ownable(msg.sender) {
    require(usdcAddress != address(0), "USDC address required");
    require(cbBtcAddress != address(0), "cbBTC address required");
    require(treasuryAddress != address(0), "Treasury address required");

    usdc = IERC20(usdcAddress);
    cbBtc = IERC20(cbBtcAddress);
    treasury = treasuryAddress;
    vrfCoordinator = VRFCoordinatorV2Interface(vrfCoordinatorAddress);

    keyHash = vrfKeyHash;
    subscriptionId = vrfSubscriptionId;
    requestConfirmations = vrfRequestConfirmations;
    callbackGasLimit = vrfCallbackGasLimit;
  }

  function setTreasury(address newTreasury) external onlyOwner {
    require(newTreasury != address(0), "Treasury address required");
    treasury = newTreasury;
    emit TreasuryUpdated(newTreasury);
  }

  function setVrfConfig(
    bytes32 vrfKeyHash,
    uint64 vrfSubscriptionId,
    uint16 vrfRequestConfirmations,
    uint32 vrfCallbackGasLimit
  ) external onlyOwner {
    keyHash = vrfKeyHash;
    subscriptionId = vrfSubscriptionId;
    requestConfirmations = vrfRequestConfirmations;
    callbackGasLimit = vrfCallbackGasLimit;
    emit VrfConfigUpdated(vrfKeyHash, vrfSubscriptionId, vrfRequestConfirmations, vrfCallbackGasLimit);
  }

  function setCaseType(
    uint256 caseTypeId,
    uint256 priceUSDC,
    uint256 minReward,
    uint256 maxReward,
    bool enabled
  ) external onlyOwner {
    require(maxReward >= minReward, "Invalid reward range");
    caseTypes[caseTypeId] = CaseType({
      priceUSDC: priceUSDC,
      minReward: minReward,
      maxReward: maxReward,
      enabled: enabled
    });
    emit CaseTypeUpdated(caseTypeId, priceUSDC, minReward, maxReward, enabled);
  }

  function purchaseCase(uint256 caseTypeId) external nonReentrant returns (uint256 openingId) {
    CaseType memory caseType = caseTypes[caseTypeId];
    require(caseType.enabled, "Case disabled");
    require(caseType.priceUSDC > 0, "Case not found");

    usdc.safeTransferFrom(msg.sender, treasury, caseType.priceUSDC);

    openingId = ++nextOpeningId;
    openings[openingId] = Opening({
      buyer: msg.sender,
      caseTypeId: caseTypeId,
      rewardAmount: 0,
      rewarded: false,
      claimed: false,
      requestId: 0
    });

    uint256 requestId = vrfCoordinator.requestRandomWords(
      keyHash,
      subscriptionId,
      requestConfirmations,
      callbackGasLimit,
      1
    );

    openings[openingId].requestId = requestId;
    requestToOpeningId[requestId] = openingId;

    emit CasePurchased(msg.sender, caseTypeId, openingId, caseType.priceUSDC);
    emit CaseRandomnessRequested(openingId, requestId);
  }

  function fulfillRandomWords(uint256 requestId, uint256[] memory randomWords) internal override {
    uint256 openingId = requestToOpeningId[requestId];
    Opening storage opening = openings[openingId];
    require(opening.buyer != address(0), "Opening not found");
    require(!opening.rewarded, "Already rewarded");

    CaseType memory caseType = caseTypes[opening.caseTypeId];
    uint256 rewardRange = caseType.maxReward - caseType.minReward + 1;
    uint256 rewardAmount = caseType.minReward + (randomWords[0] % rewardRange);

    opening.rewardAmount = rewardAmount;
    opening.rewarded = true;

    emit CaseRewarded(openingId, rewardAmount);
  }

  function claimReward(uint256 openingId) external nonReentrant {
    Opening storage opening = openings[openingId];
    require(opening.buyer == msg.sender, "Not opener");
    require(opening.rewarded, "Reward not ready");
    require(!opening.claimed, "Already claimed");

    opening.claimed = true;
    cbBtc.safeTransfer(msg.sender, opening.rewardAmount);

    emit CaseClaimed(openingId, msg.sender, opening.rewardAmount);
  }

  function getOpening(uint256 openingId) external view returns (Opening memory) {
    return openings[openingId];
  }
}

contracts/CaseStaking.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";
import { ReentrancyGuard } from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { XCase } from "./XCase.sol";

contract CaseStaking is Ownable, ReentrancyGuard {
  using SafeERC20 for IERC20;

  IERC20 public immutable caseToken;
  XCase public immutable xCase;

  event Staked(address indexed user, uint256 amount);
  event Unstaked(address indexed user, uint256 amount);

  constructor(address caseTokenAddress, address xCaseAddress) Ownable(msg.sender) {
    require(caseTokenAddress != address(0), "CASE address required");
    require(xCaseAddress != address(0), "xCASE address required");
    caseToken = IERC20(caseTokenAddress);
    xCase = XCase(xCaseAddress);
  }

  function stake(uint256 amount) external nonReentrant {
    require(amount > 0, "Amount required");
    caseToken.safeTransferFrom(msg.sender, address(this), amount);
    xCase.mint(msg.sender, amount);
    emit Staked(msg.sender, amount);
  }

  function unstake(uint256 amount) external nonReentrant {
    require(amount > 0, "Amount required");
    xCase.burn(msg.sender, amount);
    caseToken.safeTransfer(msg.sender, amount);
    emit Unstaked(msg.sender, amount);
  }
}

contracts/XCase.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import { ERC20 } from "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";

contract XCase is ERC20, Ownable {
  address public minter;

  modifier onlyMinter() {
    require(msg.sender == minter, "Not minter");
    _;
  }

  constructor() ERC20("Staked Case", "xCASE") Ownable(msg.sender) {}

  function setMinter(address newMinter) external onlyOwner {
    minter = newMinter;
  }

  function mint(address to, uint256 amount) external onlyMinter {
    _mint(to, amount);
  }

  function burn(address from, uint256 amount) external onlyMinter {
    _burn(from, amount);
  }
}

contracts/XCaseStaking.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";
import { ReentrancyGuard } from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

contract XCaseStaking is Ownable, ReentrancyGuard {
  using SafeERC20 for IERC20;

  IERC20 public immutable xCase;
  IERC20 public immutable rewardToken;

  uint256 public constant ACC_PRECISION = 1e12;
  uint256 public accRewardPerShare;
  uint256 public totalStaked;
  uint256 public unallocatedRewards;

  mapping(address => uint256) public stakedBalance;
  mapping(address => uint256) public rewardDebt;

  event Staked(address indexed user, uint256 amount);
  event Unstaked(address indexed user, uint256 amount);
  event RewardClaimed(address indexed user, uint256 amount);
  event RewardNotified(uint256 amount);

  constructor(address xCaseAddress, address rewardTokenAddress) Ownable(msg.sender) {
    require(xCaseAddress != address(0), "xCASE address required");
    require(rewardTokenAddress != address(0), "Reward token address required");
    xCase = IERC20(xCaseAddress);
    rewardToken = IERC20(rewardTokenAddress);
  }

  function pendingRewards(address user) public view returns (uint256) {
    uint256 currentAcc = accRewardPerShare;
    if (totalStaked > 0 && unallocatedRewards > 0) {
      currentAcc += (unallocatedRewards * ACC_PRECISION) / totalStaked;
    }
    return (stakedBalance[user] * currentAcc) / ACC_PRECISION - rewardDebt[user];
  }

  function stake(uint256 amount) external nonReentrant {
    require(amount > 0, "Amount required");
    _updateAccRewardPerShare();
    _harvest(msg.sender);

    xCase.safeTransferFrom(msg.sender, address(this), amount);
    stakedBalance[msg.sender] += amount;
    totalStaked += amount;
    rewardDebt[msg.sender] = (stakedBalance[msg.sender] * accRewardPerShare) / ACC_PRECISION;

    emit Staked(msg.sender, amount);
  }

  function unstake(uint256 amount) external nonReentrant {
    require(amount > 0, "Amount required");
    require(stakedBalance[msg.sender] >= amount, "Insufficient stake");

    _updateAccRewardPerShare();
    _harvest(msg.sender);

    stakedBalance[msg.sender] -= amount;
    totalStaked -= amount;
    rewardDebt[msg.sender] = (stakedBalance[msg.sender] * accRewardPerShare) / ACC_PRECISION;

    xCase.safeTransfer(msg.sender, amount);

    emit Unstaked(msg.sender, amount);
  }

  function claim() external nonReentrant {
    _updateAccRewardPerShare();
    _harvest(msg.sender);
    rewardDebt[msg.sender] = (stakedBalance[msg.sender] * accRewardPerShare) / ACC_PRECISION;
  }

  function notifyRewardAmount(uint256 amount) external onlyOwner {
    require(amount > 0, "Amount required");
    rewardToken.safeTransferFrom(msg.sender, address(this), amount);

    if (totalStaked == 0) {
      unallocatedRewards += amount;
    } else {
      accRewardPerShare += (amount * ACC_PRECISION) / totalStaked;
    }

    emit RewardNotified(amount);
  }

  function _updateAccRewardPerShare() internal {
    if (totalStaked > 0 && unallocatedRewards > 0) {
      accRewardPerShare += (unallocatedRewards * ACC_PRECISION) / totalStaked;
      unallocatedRewards = 0;
    }
  }

  function _harvest(address user) internal {
    uint256 pending = (stakedBalance[user] * accRewardPerShare) / ACC_PRECISION - rewardDebt[user];
    if (pending > 0) {
      rewardToken.safeTransfer(user, pending);
      emit RewardClaimed(user, pending);
    }
  }
}

